<?php

namespace Drupal\twig_placeholders\Twig;

use Drupal\Core\Messenger\MessengerInterface;
use Symfony\Component\DependencyInjection\ContainerInterface;
use Twig\TwigFunction;
use Twig\Extension\AbstractExtension;

/**
 * Provides a Twig function for generating placeholder logos.
 */
class LogoExtension extends AbstractExtension {

  /**
   * {@inheritdoc}
   */
  public function getFunctions(): array {
    return [
      new TwigFunction('tp_logo', $this->selectPlaceholderLogo(...)),
    ];
  }

  /**
   * The messenger service.
   *
   * @var \Drupal\Core\Messenger\MessengerInterface
   */
  protected MessengerInterface $messenger;

  /**
   * Constructs a LogoExtension object.
   *
   * @param \Drupal\Core\Messenger\MessengerInterface $messenger
   *   The messenger service.
   */
  public function __construct(MessengerInterface $messenger) {
    $this->messenger = $messenger;
  }

  /**
   * {@inheritdoc}
   */
  public static function create(ContainerInterface $container): self {
    return new self(
      $container->get('messenger') instanceof MessengerInterface ? $container->get('messenger') : throw new \InvalidArgumentException('The messenger service must implement MessengerInterface.')
    );
  }

  public const ALLOWED_SVG_CATS = ['badge', 'gram', 'type'];

  /**
   * Validates if the provided category is allowed.
   *
   * @param string $category
   *   The category to validate.
   *
   * @return bool
   *   TRUE if the category is valid, FALSE otherwise.
   */
  public static function isValidCategory(string $category): bool {
    return in_array($category, self::ALLOWED_SVG_CATS, TRUE);
  }

