  /**
   * Selects a placeholder logo <svg>.
   *
   * @param string|int|null $category_or_id
   *   The category of the logo from ['badge', 'gram', 'type'],
   *   or logo ID [index of logo in array of $logos] [0-15],
   *   or NULL (optional).
   *
   * @return array<string,mixed>
   *   The generated placeholder SVG as a Drupal render array.
   */
  public function selectPlaceholderLogo(
    mixed $category_or_id = NULL,
  ): array {
    // Initialise with a random logo.
    $id = array_rand($this->logos);
    $markup = $this->logos[$id]['markup'];

    if ($category_or_id !== NULL && is_int($category_or_id)) {
      // A logo ID was provided.
      if (isset($this->logos[$category_or_id])) {
        $markup = $this->logos[$category_or_id]['markup'];
      }
      else {
        $allowed_ids = count($this->logos) - 1;

        $this->messenger->addWarning(sprintf(
          'Invalid SVG ID "%s" provided to tp_logo(). Falling back to using a random ID. Allowed IDs: 0-%s (inclusive).',
          $category_or_id,
          $allowed_ids
        ));
      }
    }

    if ($category_or_id !== NULL && is_string($category_or_id)) {
      // A logo category was provided.
      if (self::isValidCategory($category_or_id)) {
        $filtered_logos = array_filter($this->logos, fn ($logo) => $logo['category'] === $category_or_id);

        if (!empty($filtered_logos)) {
          // Re-index the array.
          $filtered_logos = array_values($filtered_logos);

          $id = array_rand($filtered_logos);

          $markup = $filtered_logos[$id]['markup'];
        }
      }
      else {
        $allowed_cats = implode(', ', self::ALLOWED_SVG_CATS);

        $this->messenger->addWarning(sprintf(
          'Invalid SVG category "%s" provided to tp_logo(). Falling back to default category of NULL. Allowed extensions: %s.',
          $category_or_id,
          $allowed_cats
        ));
      }
    }

    return [
      '#markup' => $markup,
      '#allowed_tags' => ['svg', 'path', 'circle', 'rect', 'g', 'defs', 'style'],
    ];
  }

